INSERT INTO question_book VALUES ('exam20220424', '3.데이터베이스 구축', 42, '데이터베이스의 트랜잭션 성질들 중에서 다음 설명에 해당하는 것은?', '/resources/qstImg/q14.jpg', 'Atomicity', 'Consistency', 'Isolation', 'Durability', 1);
INSERT INTO question_book VALUES ('exam20220424', '3.데이터베이스 구축', 43, '분산 데이터베이스 시스템과 관련한 설명으로 틀린 것은?', 'null', '물리적으로 분산된 데이터베이스 시스템을 논리적으로 하나의 데이터베이스 시스템처럼 사용할 수 있도록 한 것이다.', '물리적으로 분산되어 지역별로 필요한 데이터를 처리할 수 있는 지역 컴퓨터(Local Computer)를 분산 처리기(Distributed Processor)라고 한다.', '분산 데이터베이스 시스템을 위한 통신 네트워크 구조가 데이터 통신에 영향을 주므로 효율적으로 설계해야 한다.', '데이터베이스가 분산되어 있음을 사용자가 인식할 수 있도록 분산 투명성(Distribution Transparency)을 배제해야 한다.', 4);
INSERT INTO question_book VALUES ('exam20220424', '3.데이터베이스 구축', 44, '다음 테이블을 보고 강남지점의 판매량이 많은 제품부터 출력되도록 할 때 다음 중 가장 적절한 SQL 구문은? (단, 출력은 제품명과 판매량이 출력되도록 한다', '/resources/qstImg/q15.jpg', 'SELECT 제품명, 판매량 FROM 푸드ORDER BY 판매량 ASC;', 'SELECT 제품명, 판매량 FROM 푸드ORDER BY 판매량 DESC;', 'SELECT 제품명, 판매량 FROM 푸드WHERE 지점명 = "강남지점" ORDER BY 판매량 ASC;', 'SELECT 제품명, 판매량 FROM 푸드WHERE 지점명 = "강남지점" ORDER BY 판매량 DESC;', 4);
INSERT INTO question_book VALUES ('exam20220424', '3.데이터베이스 구축', 45, '데이터베이스의 인덱스와 관련한 설명으로 틀린 것은?', 'null', '문헌의 색인, 사전과 같이 데이터를 쉽고 빠르게 찾을 수 있도록 만든 데이터 구조이다.', '테이블에 붙여진 색인으로 데이터 검색 시 처리 속도 향상에 도움이 된다.', '인덱스의 추가, 삭제 명령어는 각각 ADD, DELETE이다.', '대부분의 데이터베이스에서 테이블을 삭제하면 인덱스도 같이 삭제된다', 3);
INSERT INTO question_book VALUES ('exam20220424', '3.데이터베이스 구축', 46, '물리적 데이터베이스 구조의 기본 데이터 단위인 저장 레코드의 양식을 설계할 때 고려 사항이 아닌 것은?', 'null', '데이터 타입', '데이터 값의 분포', '트랜잭션 모델링', '접근 빈도', 3);
INSERT INTO question_book VALUES ('exam20220424', '3.데이터베이스 구축', 47, 'SQL의 기능에 따른 분류 중에서 REVOKE문과 같이 데이터의 사용 권한을 관리하는데 사용하는 언어는?', 'null', 'DDL(Data Definition Language)', 'DML(Data Manipulation Language)', 'DCL(Data Control Language)', 'DUL(Data User Language)', 3);
INSERT INTO question_book VALUES ('exam20220424', '3.데이터베이스 구축', 48, '데이터 사전에 대한 설명으로 틀린 것은?', 'null', '시스템 카탈로그 또는 시스템 데이터베이스라고도 한다.', '데이터 사전 역시 데이터베이스의 일종이므로 일반 사용자가 생성, 유지 및 수정 할 수 있다.', '데이터베이스에 대한 데이터인 메타데이터(Metadata)를 저장하고 있다.', '데이터 사전에 있는 데이터에 실제로 접근하는 데 필요한 위치 정보는 데이터 디렉토리(Data Directory)라는 곳에서 관리한다.', 2);
INSERT INTO question_book VALUES ('exam20220424', '3.데이터베이스 구축', 49, '데이터베이스에서 릴레이션에 대한 설명으로 틀린 것은?', 'null', '모든 튜플은 서로 다른 값을 가지고 있다.', '하나의 릴레이션에서 튜플은 특정한 순서를 가진다.', '각 속성은 릴레이션 내에서 유일한 이름을 가진다.', '모든 속성 값은 원자 값(atomic value)을 가진다.', 2);
INSERT INTO question_book VALUES ('exam20220424', '3.데이터베이스 구축', 50, '데이터베이스에서의 뷰(View)에 대한 설명으로 틀린 것은?', 'null', '뷰는 다른 뷰를 기반으로 새로운 뷰를 만들 수 있다.', '뷰는 일종의 가상 테이블이며, update에는 제약이 따른다.', '뷰는 기본 테이블을 만드는 것처럼 create view를 사용하여 만들 수 있다.', '뷰는 논리적으로 존재하는 기본 테이블과 다르게 물리적으로만 존재하며 카탈로그에 저장된다.', 4);
INSERT INTO question_book VALUES ('exam20220424', '3.데이터베이스 구축', 51, '트랜잭션의 상태 중 트랜잭션의 마지막 연산이 실행된 직후의 상태로, 모든 연산의 처리는 끝났지만 트랜잭션이 수행한 최종 결과를 데이터베이스에 반영하지 않은 상태는?', 'null', 'Active', 'Partially Committed', 'Committed', 'Aborted', 2);
INSERT INTO question_book VALUES ('exam20220424', '3.데이터베이스 구축', 52, 'SQL의 명령을 사용 용도에 따라 DDL, DML, DCL로 구분할 경우, 그 성격이 나머지 셋과 다른 것은?', 'null', 'SELECT', 'UPDATE', 'INSERT', 'GRANT', 4);
INSERT INTO question_book VALUES ('exam20220424', '3.데이터베이스 구축', 53, '키의 종류 중 유일성과 최소성을 만족하는 속성 또는 속성들의 집합은?', 'null', 'Atomic key', 'Super key', 'Candidate key', 'Test key', 3);
INSERT INTO question_book VALUES ('exam20220424', '3.데이터베이스 구축', 54, '데이터베이스에서 개념적 설계 단계에 대한 설명으로 틀린', 'null', '산출물로 E-R Diagram을 만들 수 있다.', 'DBMS에 독립적인 개념 스키마를 설계한다.', '트랜잭션 인터페이스를 설계 및 작성한다.', '논리적 설계 단계의 앞 단계에서 수행된다', 3);
INSERT INTO question_book VALUES ('exam20220424', '3.데이터베이스 구축', 55, '테이블의 기본키(Primary Key)로 지정된 속성에 관한 설명으로 가장 거리가 먼 것은?', 'null', 'NOT NULL로 널 값을 가지지 않는다.', '릴레이션에서 튜플을 구별할 수 있다.', '외래키로 참조될 수 있다.', '검색할 때 반드시 필요하다', 4);
