INSERT INTO question_book VALUES ('exam20220424', '4.프로그래밍 언어활용', 61, 'C언어에서 문자열 처리 함수의 서식과 그 기능의 연결로 틀린 것은?', 'null', 'strlen(s) - s의 길이를 구한다.', 'strcpy(s1, s2) - s2를 s1으로 복사한다.', 'strcmp(s1, s2) - s1과 s2를 연결한다.', 'strrev(s)－s를 거꾸로 변환한다.', 3);
